(()=>{var i=document.querySelectorAll(".code-header .copy-code-below");i.forEach(e=>{e.addEventListener("click",()=>{let o=e.nextElementSibling;if(!o)return;let c=o.querySelector("code"),t=c?c.innerText:o.innerText;t=t.trim(),navigator.clipboard.writeText(t).then(()=>{e.innerHTML="Copied",setTimeout(()=>{e.innerHTML='<i class="fa-solid fa-copy"></i>'},1500)}).catch(r=>{console.error("Failed to copy code:",r)})})});})();
